mapper.select:
SELECT id, content_id, image_path, image_order, caption, created_at
FROM content_images 
WHERE id = #{imageId}

mapper.update:
UPDATE content_images 
SET 
    image_path = #{imagePath},
    image_order = #{imageOrder},
    caption = #{caption}
WHERE id = #{id}

mapper.select:
SELECT MAX(image_order) 
FROM content_images 
WHERE content_id = #{contentId}

mapper.update:

UPDATE content_images c1
SET image_order = (
    SELECT COUNT(*) 
    FROM (SELECT * FROM content_images) c2 
    WHERE c2.content_id = #{contentId} 
    AND c2.created_at <= c1.created_at
)
WHERE content_id = #{contentId}

mapper.select:
SELECT COUNT(*) 
FROM content_images 
WHERE content_id = #{contentId}

mapper.update:
UPDATE content_images 
SET image_order = #{newOrder}
WHERE id = #{imageId}
