mapper.update:
UPDATE hottalk_post 
SET views = views + 1 
WHERE id = #{id}

mapper.insert:
INSERT INTO hottalk_post (
    userid, userip, nickname, passwd, title, content, 
    views, photo1, photo2, photo3, likes, dislikes, reports, created_at
) VALUES (
    #{userid}, #{userip}, #{nickname}, #{passwd}, #{title}, #{content},
    #{views}, #{photo1}, #{photo2}, #{photo3}, #{likes}, #{dislikes}, #{reports}, #{createdAt}
)

mapper.update:
UPDATE hottalk_post 
SET likes = #{likes}, dislikes = #{dislikes}
