mapper.select:
SELECT log_id as logId, user_id as userId, old_level as oldLevel, new_level as newLevel, gained_exp as gainedExp, created_at as createdAt
FROM level_log
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT #{limit}

SELECT log_id as logId, user_id as userId, old_level as oldLevel, new_level as newLevel, gained_exp as gainedExp, created_at as createdAt
FROM level_log
WHERE log_id = #{logId}

SELECT COUNT(*)
FROM level_log
WHERE user_id = #{userId}
