mapper.select:
SELECT 
FROM hottalk_post 
WHERE category_id = #{categoryId}
ORDER BY created_at DESC 
LIMIT #{offset}, #{limit}

mapper.insert:
INSERT INTO hottalk_post (
    category_id, userid, userip, title, content, 
    photo1, photo2, photo3, views, likes, dislikes, 
    created_at, nickname, passwd, reports
) VALUES (
    #{category_id}, #{userid}, #{userip}, #{title}, #{content}, 
    #{photo1}, #{photo2}, #{photo3}, #{views}, #{likes}, #{dislikes}, 
    NOW(), #{nickname}, #{passwd}, #{reports}
)

mapper.update:
UPDATE hottalk_post 
SET status = '삭제', updated_at = NOW()
WHERE id = #{id}

UPDATE hottalk_post 
SET view_count = view_count + 1, updated_at = NOW()
WHERE id = #{id}

UPDATE hottalk_post 
SET views = views + 1
WHERE id = #{id}

UPDATE hottalk_post 
SET reports = reports + 1, updated_at = NOW()
WHERE id = #{id}

UPDATE hottalk_post 
SET like_count = #{likeCount}, updated_at = NOW()
WHERE id = #{id}

UPDATE hottalk_post 
SET comment_count = #{commentCount}, updated_at = NOW()
WHERE id = #{id}

UPDATE hottalk_post 
SET status = #{status}, updated_at = NOW()
WHERE id = #{id}

UPDATE hottalk_post 
SET is_notice = #{isNotice}, updated_at = NOW()
WHERE id = #{id}

UPDATE hottalk_post 
SET is_pinned = #{isPinned}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) 
FROM hottalk_post 
WHERE category_id = #{categoryId}

SELECT 
FROM hottalk_post 
WHERE category_id = #{categoryId}
ORDER BY likes DESC, created_at DESC
